declare module "cinemeta.js"

export interface expectedOutputAll {
      _id: string,
      imdb_id: string,
      name: string,
      type: 'movie'|'series',
      year: string
    }
export interface expectedOutputMovie {
        _id: string,
        imdb_id: string,
        name: string,
        type: 'movie',
        year: string
}

export interface expectedOutputSeries {
    _id: string,
    imdb_id: string,
    name: string,
    type: 'series',
    year: string
}

export function search(query: string): Promise<expectedOutputAll[]>
export function searchMovie(query: string): Promise<expectedOutputMovie[]>
export function searchSeries(query: string): Promise<expectedOutputSeries[]>
export function searchOTBOYearMovie(query: string): Promise<expectedOutputMovie[]>
export function searchOTBOYearSeries(query: string): Promise<expectedOutputSeries[]>
export function searchOTBOYear(query: string): Promise<expectedOutputAll[]>
export function randomMovie(): Promise<expectedOutputMovie>
export function randomSeries(): Promise<expectedOutputSeries>
export function random(): Promise<expectedOutputAll>